mapper.select:
SELECT
FROM `USER` WHERE IS_DELETED = 0
AND ID = #{request.id}

SELECT

FROM `USER`
WHERE
IS_DELETED = 0

  AND USER_NAME = #{request.username}


  AND PASSWORD = #{request.password}

mapper.update:
UPDATE
`USER`
SET
`IS_DELETED` = 1
,`LAST_UPDATED_BY`=#{ request.userId }
,`LAST_UPDATE_TIME`=SYSDATE()
WHERE
`IS_DELETED`=0
AND `TENANT_ID`=#{ request.tenantId }
AND `ID` = #{id}

mapper.select:
SELECT
FROM `USER` WHERE IS_DELETED = 0
AND PHONE = #{request.phone}
